( 'NDDS 1&2 Months', '', '', '2016-04-11', '10:00:00', NULL, 1, '<html>\r\n	<head>\r\n		<META http-equiv="Content-Type" content="text/html; charset=UTF-8">\r\n		<title>NDDS_1_2month</title>\r\n\r\n		<!-- OSCAR based files for greater functionality -->\r\n		<script language="javascript">\r\n			function formPrint(){\r\n				window.print();\r\n			}\r\n		</script>\r\n\r\n		<!-- scripts to confirm closing of window if it hadnt been saved yet -->\r\n		<script language="javascript">\r\n			//keypress events trigger dirty flag\r\n			var needToConfirm = false;\r\n			\r\n			document.onkeyup=setDirtyFlag;\r\n			function setDirtyFlag(){\r\n				needToConfirm = true;\r\n			}\r\n			function releaseDirtyFlag(){\r\n				needToConfirm = false; //Call this function to prevent an alert.\r\n				//this could be called when save button is clicked\r\n			}\r\n			window.onbeforeunload = confirmExit;\r\n			function confirmExit(){\r\n				if (needToConfirm){\r\n					return "You have attempted to leave this page. If you have made any changes to the fields without clicking the Save button, your changes will be lost. Are you sure you want to exit this page?";\r\n				}\r\n			}\r\n\r\n			function set_window_size() {\r\n				top.window.moveTo(0,0);\r\n				if (document.all) {\r\n					top.window.resizeTo(1000,900);\r\n				}\r\n				else if (document.layers||document.getElementById) {\r\n					if (top.window.outerHeight<screen.availHeight||top.window.outerWidth<screen.availWidth){\r\n						top.window.outerHeight = 900;\r\n						top.window.outerWidth = 1000;\r\n					}\r\n				}\r\n			}\r\n\r\n			function draftSave() {\r\n				$("#subject").val(draftCopy);\r\n				needToConfirm = false;\r\n			}\r\n\r\n			function setFilledPrompt() {\r\n				//set filled1m/filled2m="Yes" if anything is checked in 1-month/2-month section\r\n				var elements = document.getElementsByTagName("input");\r\n				for (var i=0; i<elements.length; i++) {\r\n					if (elements[i].checked) {\r\n						var header = elements[i].name.substring(0, 2);\r\n						if (header=="M1") $("#filled1m").val("Yes");\r\n						else if (header=="M2") $("#filled2m").val("Yes");\r\n					}\r\n					if ($("#filled1m").val()=="Yes" && $("#filled2m").val()=="Yes") break;\r\n				}\r\n				\r\n				//setup proper prompts - must have the rightly named hidden fields for RBR visit dates and subject\r\n				//Ronnie 2015-07-21:\r\n				//If anything is checked in 2-month section, prompt RBR 2-month; otherwise prompt RBR 1-month\r\n				if ($("#filled2m").val()=="Yes") setupPrompts(''NDDS_1_2month'', ''2m'');\r\n				else setupPrompts(''NDDS_1_2month'', ''1m'');\r\n			}\r\n\r\n			function checkAllYes() {\r\n				Clearcheckbox();\r\n				var i = 0;\r\n				var x = 0;\r\n				for(i=0;i<=4;i++){\r\n					$("#M1Q"+i+"_Yes").prop(''checked'',true);\r\n				}\r\n\r\n				for(x=0;x<=13;x++){\r\n					$("#M2Q"+x+"_Yes").prop(''checked'',true);\r\n				}\r\n			}\r\n\r\n			function Clearcheckbox() {\r\n\r\n				$(''input[type="checkbox"]'').each(function (){\r\n					$(this).prop(''checked'',false);\r\n				});\r\n			}\r\n\r\n			function click_one(one, two, three, four,five){\r\n				if (one.checked){\r\n					two.checked = false;\r\n					three.checked = false;\r\n					four.checked = false;\r\n					if(five != "NA"){\r\n						five.checked = false;\r\n					}\r\n				}\r\n			}\r\n\r\n			function printResource(sheet){\r\n				printResourceSheet("${oscar_image_path}/"+sheet);\r\n			}\r\n			\r\n			function populatePrevious1mData() {\r\n				var fields = ["M1Q1_Yes","M1Q1_No","M1Q1_Sometimes","M1Q1_Not_Sure","M1Q2_Yes","M1Q2_No","M1Q2_Sometimes","M1Q2_Not_Sure","M1Q2_Have_Not_Tried","M1Q3_Yes","M1Q3_No","M1Q3_Sometimes","M1Q3_Not_Sure","M1Q3_Have_Not_Tried","M1Q4_Yes","M1Q4_No","M1Q4_Sometimes","M1Q4_Not_Sure","M1Q4_Have_Not_Tried"];\r\n				for (var i=0; i<fields.length; i++) {\r\n					if ($("#last"+fields[i]).val()=="on") $("#"+fields[i]).prop("checked", true);\r\n				}\r\n			}\r\n\r\n		</script>\r\n\r\n\r\n		<link href="${oscar_image_path}/NDDS_new.css" rel="stylesheet" type="text/css">\r\n		<script type="text/javascript" src="${oscar_image_path}/NDDS_prompts.js"></script>\r\n		\r\n		 \r\n		<link href="../css/datepicker.css" rel="stylesheet" type="text/css">\r\n		<link rel="stylesheet" href="../css/cupertino/jquery-ui-1.8.18.custom.css">\r\n\r\n\r\n		<script type="text/javascript" src="../js/jquery-1.9.1.min.js"></script>\r\n		<script type="text/javascript" src="../js/jquery.validate.js"></script>\r\n		<script type="text/javascript" language="JavaScript" src="../share/javascript/Oscar.js"></script>\r\n\r\n\r\n		<link href="../library/bootstrap2-datepicker/datepicker3.css" rel="stylesheet">\r\n		<script src="../library/bootstrap2-datepicker/bootstrap-datepicker.js"></script>\r\n\r\n		<script>\r\n			\r\n			$( document ).ready(function() {\r\n \r\n				$("#draft").hide();   \r\n				if($("#subject").val() == draftCopy){\r\n					$("#draft").show();\r\n					$("#subject").val("");\r\n				}else if($("#subject").val() != ""){\r\n					$("#save").hide();\r\n				}else{\r\n					$("#subject").hide();   \r\n				}\r\n\r\n				set_window_size();\r\n				populatePrevious1mData();\r\n\r\n\r\n				if ($("#dob").val()=="") {\r\n					$("#dob").val($("#birthdayY").val() + "-" + $("#birthdayM").val() + "-" + $("#birthdayD").val());\r\n				}\r\n				$("#dobtext").html($("#birthdayY").val() + "-" + $("#birthdayM").val() + "-" + $("#birthdayD").val());\r\n\r\n				var submitted = false;\r\n				$("#NDDS_1_2month").validate({\r\n					rules:{\r\n						txtChildName: {\r\n							required:true\r\n						},\r\n						dob:{\r\n							required:true,\r\n							date:true\r\n						},\r\n						today:{\r\n							required:true,\r\n							date:true\r\n						}\r\n					},\r\n					messages: {\r\n						txtChildName: "Please enter a child''s name.",\r\n						dob: "Please enter a valid date of birth for the child.",\r\n						today: "Please enter a valid date for today."\r\n					},\r\n					showErrors: function(errorMap,errorList){\r\n						if(submitted){\r\n							var summary = "You have the following errors: \\n";\r\n							$.each(errorList, function() { summary += " * " + this.message + "\\n" });\r\n\r\n							submitted=false;\r\n							alert(summary);\r\n\r\n						}\r\n						this.defaultShowErrors();\r\n					},\r\n					invalidHandler: function(form, validator){\r\n						submitted = true;\r\n					},\r\n					errorPlacement: function (error,element){\r\n						return true;\r\n					}\r\n				});\r\n\r\n			});\r\n\r\n		</script>\r\n	</head>\r\n\r\n	<body>\r\n\r\n  \r\n		<form method="post" name="NDDS_1_2month" id="NDDS_1_2month" >\r\n		\r\n			<!-- for prompts -->\r\n			<input type="hidden" id="rbr_1m_visit_date" name="rbr_1m_visit_date" oscarDB="e$last#visit_date_1m" eform$name="Rourke Baby Record"/>\r\n			<input type="hidden" id="rbr_2m_visit_date" name="rbr_2m_visit_date" oscarDB="e$last#visit_date_2m" eform$name="Rourke Baby Record"/>\r\n			<input type="hidden" id="rbr_subject" name="rbr_subject" oscarDB="e$last#subject" eform$name="Rourke Baby Record"/>\r\n\r\n			<input type="hidden" id="filled1m" name="filled1m"/>\r\n			<input type="hidden" id="filled2m" name="filled2m"/>\r\n\r\n			<input type="hidden" name="frm_creator" id="frm_creator" oscarDB="current_user" />\r\n			<input type="hidden" name="patient_mrp" id="patient_mrp" oscarDB="doctor_provider_no" />\r\n			<input type="hidden" name="demographic_no" id="demographic_no" oscarDB="patient_id" />\r\n\r\n\r\n			<input type="hidden" name="birthdayY" id="birthdayY" oscarDB="dob_year"/>\r\n			<input type="hidden" name="birthdayM" id="birthdayM" oscarDB="dob_month"/>\r\n			<input type="hidden" name="birthdayD" id="birthdayD" oscarDB="dob_day"/>\r\n\r\n			<div id="page1" style="page-break-after:always;position:relative;" >\r\n\r\n				<img id="BGImage1" src="${oscar_image_path}/NDDS_1_2month.png" style="position: relative; left: 0px; top: 0px; width:800px;">\r\n\r\n			</div>\r\n\r\n			<div style="position: absolute; top:15px; left:240px;">\r\n\r\n				<label for="txtChildName">Child''s Name:</label>\r\n				<inpu