( 'NDDS 4 Years', '', '', '2016-04-11', '10:00:00', NULL, 1, '<html>\r\n<head>\r\n<META http-equiv="Content-Type" content="text/html; charset=UTF-8">\r\n<title>NDDS_4_Year</title>\r\n\r\n\r\n<!-- OSCAR based files for greater functionality -->\r\n<script language="javascript">\r\nfunction formPrint(){\r\n	window.print();\r\n} \r\n</script>\r\n\r\n<!-- scripts to confirm closing of window if it hadnt been saved yet -->\r\n<script language="javascript">\r\n//keypress events trigger dirty flag\r\nvar needToConfirm = false;\r\n\r\n\r\ndocument.onkeyup=setDirtyFlag;\r\nfunction setDirtyFlag(){\r\n	needToConfirm = true;\r\n}\r\nfunction releaseDirtyFlag(){\r\n	needToConfirm = false; //Call this function to prevent an alert.\r\n}\r\nwindow.onbeforeunload = confirmExit;\r\nfunction confirmExit(){\r\n	if (needToConfirm){\r\n		 return "You have attempted to leave this page. If you have made any changes to the fields without clicking the Save button, your changes will be lost. Are you sure you want to exit this page?";\r\n	 }\r\n}\r\n	\r\n\r\n\r\nfunction set_window_size() {\r\n	top.window.moveTo(0,0);\r\n	if (document.all) {\r\n		top.window.resizeTo(1000,900);\r\n	}\r\n	else if (document.layers||document.getElementById) {\r\n		if (top.window.outerHeight<screen.availHeight||top.window.outerWidth<screen.availWidth){\r\n			top.window.outerHeight = 900;\r\n			top.window.outerWidth = 1000;\r\n		}\r\n	}\r\n}\r\n\r\n\r\nfunction draftSave() {\r\n	$("#subject").val(draftCopy);\r\n	needToConfirm = false;\r\n}\r\n\r\n// function isAllPicked() {\r\n// 	var picked = {	q1:false,	q2:false,	q3:false,	q4:false,	q5:false,\r\n// 					q6:false,	q7:false,	q8:false,	q9:false,	q10:false,\r\n// 					q11:false,	q12:false,	q13:false,	q14:false,	q15:false,\r\n// 					q16:false,	q17:false,	q18:false,	q19:false,	q20:false };\r\n// 	var elements = document.getElementsByTagName("input");\r\n// 	for (var i=0; i<elements.length; i++) {\r\n// 		if (elements[i].checked) {\r\n// 			var label = elements[i].name